        R2.bottom = A.bottom;
        R2.left   = A.right - A.right_margin + DX - 1;
        R2.right  = R2.left;

        R1.top    = R2.top;
        R1.bottom = R2.bottom;
        R1.left   = R2.left - 2;
        R1.right  = R1.left;

        A.right_margin = DX;
